import React, { useState } from 'react';

const Register = () => {
  const [displayName, setDisplayName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can use displayName, email, and password as needed
    console.log('Submitted values:', displayName, email, password);
    // Example: Perform form submission logic (e.g., API call) here
  }

  return (
    <div className="formContainer">
      <div className="formWrapper">
        <span className="logo">Rohit Chat</span>
        <span className="title">Register</span>
        <form onSubmit={handleSubmit}>
          <input
            required
            type="text"
            placeholder="display name"
            value={displayName}
            onChange={(e) => setDisplayName(e.target.value)}
          />
          <input
            required
            type="email"
            placeholder="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            required
            type="password"
            placeholder="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button disabled={loading}>Sign up</button>
        </form>
        <p>You already have an account? Login</p>
      </div>
    </div>
  );
}

export default Register;
